INSERT INTO `wp_posts` VALUES (2197,1,'2015-05-11 23:45:06','2015-05-11 20:45:06','<strong>Описание:</strong>\r\nТип покрытия – рутилово-основное.\r\n\r\nЭлектрод двойного назначения, обеспечивающий в наплавке коррозионностойкую аустенитную хромо-никель-марганцовистую сталь. Второе – наплавка поверхностных слоев стойких к окалинообразованию и общей коррозии. Они также применяются для наплавки с последующим механическим упрочнением торцевых уплотнений запорной арматуры и седел клапанов, работающих в контакте с относительно агрессивными средами при температурах до 600°С. Электроды могут применяться для наплавки переходных слоев перед выполнением упрочняющей наплавки на стали с ограниченной свариваемостью. Механическая обрабатываемость наплавленного слоя очень хорошая, коррозионная стойкость очень хорошая, стойкость к трению металла о металл после упрочнения очень хорошая, стойкость к высоким температурам хорошая.\r\nТок: ~ / = (+)\r\nПространственные положения при сварке: 1, 2, 3, 4, 6\r\nНапряжение холостого хода: 65 В\r\nРежимы прокалки: 330-370°С, 2 часа\r\n<table border=\"1\" width=\"100%\" cellpadding=\"3\">\r\n<tbody>\r\n<tr>\r\n<td>Классификация</td>\r\n<td>Сертификация</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<div>EN 14700: E Fe10\r\nDIN 8555: E 8-UM-200-CKZ (условно)</div></td>\r\n<td>-</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<div style=\"text-align: center;\"><b>Химический состав</b></div>\r\n<div>\r\n<table border=\"1\" width=\"100%\" cellspacing=\"1\" cellpadding=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td>С</td>\r\n<td>Mn</td>\r\n<td>Si</td>\r\n<td>Cr</td>\r\n<td>Ni</td>\r\n<td>P</td>\r\n<td>S</td>\r\n</tr>\r\n<tr>\r\n<td>0.08</td>\r\n<td>5.40</td>\r\n<td>0.80</td>\r\n<td>18.4</td>\r\n<td>9.1</td>\r\n<td>max 0.035</td>\r\n<td>max 0.020</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<div style=\"text-align: center;\"><b>Механические свойства</b></div>\r\n<div>\r\n<table border=\"1\" width=\"100%\" cellpadding=\"3\">\r\n<tbody>\r\n<tr>\r\n<td>Твердость наплавленного металла после сварки (без предварительного подогрева, межпроходная температура &lt;150°C)</td>\r\n<td> ~180 HB</td>\r\n</tr>\r\n<tr>\r\n<td colspan=\"1\">Твердость наплавленного металла после механического упрочнения (холодная деформация &gt;30%)</td>\r\n<td colspan=\"1\">~380 HB</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<div style=\"text-align: center;\"><b>Информация для заказа</b></div>\r\n<div>\r\n<table border=\"1\" width=\"100%\" cellpadding=\"3\">\r\n<tbody>\r\n<tr>\r\n<td>Наименование</td>\r\n<td>Артикул</td>\r\n</tr>\r\n<tr>\r\n<td colspan=\"1\">OK 67.43 2.5x300mm 1/4 VP</td>\r\n<td colspan=\"1\">67432520L0</td>\r\n</tr>\r\n<tr>\r\n<td colspan=\"1\">OK 67.43 3.2x350mm 1/2 VP</td>\r\n<td colspan=\"1\">67433230T0</td>\r\n</tr>\r\n<tr>\r\n<td colspan=\"1\">OK 67.43 4.0x350mm 1/2 VP</td>\r\n<td colspan=\"1\">67434030G0</td>\r\n</tr>\r\n<tr>\r\n<td colspan=\"1\">OK 67.43 5.0x350mm 1/2 VP</td>\r\n<td colspan=\"1\">67435040G0</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</div>\r\n</div>\r\n</div>','Сварочный электрод ESAB OK 67.43','','publish','closed','open','','svarochnyj-elektrod-esab-ok-67-43','','','2015-05-12 21:25:53','2015-05-12 18:25:53','',0,'http://ngsgroup/?p=2197',0,'post','',0);
