INSERT INTO `wp_posts` VALUES (2278,1,'2015-05-12 21:45:28','2015-05-12 18:45:28','<strong>Описание:</strong>\r\nТип покрытия – основное.\r\n\r\nЭлектрод, предназначенный для выполнения корневых проходов при сварке оборудования и трубопроводов атомных электростанций, а также других видов оборудования энергетического машиностроения (котлы, сосуды и др.) из легированных теплоустойчивых хромо-молибден-ванадиевых сталей марок 12Х1МФ, 14Х1ГМФ, 15Х1М1Ф, 20ХМФЛ, W.No 1.7715, 15 CrMoV 5-10 и им аналогичных с максимальной температурой эксплуатации до 565°C. Электроды выпускаются только диаметром 2,5 мм.\r\nТок: = (+)\r\nПространственные положения при сварке: 1, 2, 3, 4, 6\r\nРежимы прокалки: 360-400°С, 2-2,5 часа\r\n\r\nС марта 2015 года электрод производится на заводе ЗАО \"ЭСАБ-СВЭЛ\", входящий в группу компаний ESAB.\r\n<table border=\"1\" width=\"100%\" cellspacing=\"1\" cellpadding=\"1\">\r\n<tbody>\r\n<tr>\r\n<td>Классификация</td>\r\n<td>Сертификация</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<div>ГОСТ 9467: Э-09Х1МФ</div>\r\n<div>ОСТ 24.948.01-90</div></td>\r\n<td>ГосАтомНадзор</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<div style=\"text-align: center;\"><b>Химический состав</b></div>\r\n<div>\r\n<table border=\"1\" width=\"100%\" cellspacing=\"1\" cellpadding=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td>С</td>\r\n<td>Mn</td>\r\n<td>Si</td>\r\n<td>Cr</td>\r\n<td>Mo</td>\r\n<td>V</td>\r\n<td>P</td>\r\n<td>S</td>\r\n</tr>\r\n<tr>\r\n<td>0.09</td>\r\n<td>0.75</td>\r\n<td>0.30</td>\r\n<td>1.00</td>\r\n<td>0.55</td>\r\n<td>0.20</td>\r\n<td>max 0.030</td>\r\n<td>max 0.025</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<div style=\"text-align: center;\"><b>Механические свойства*</b></div>\r\n<div>\r\n<table border=\"1\" width=\"100%\" cellspacing=\"1\" cellpadding=\"1\">\r\n<tbody>\r\n<tr>\r\n<td>Предел текучести σ<sub>т</sub>, Н/мм²</td>\r\n<td>Предел прочности σ<sub>в</sub>, Н/мм²</td>\r\n<td>Удлинение δ, %</td>\r\n<td>Ударная вязкость KCU, Дж/см²</td>\r\n</tr>\r\n<tr>\r\n<td>≥343</td>\r\n<td>≥490</td>\r\n<td>≥16</td>\r\n<td>\r\n<div>≥78 при +20°С</div></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<div>\r\n<div style=\"text-align: left;\"><b>*</b>После термообработки 720-750°С, 5 часов</div>\r\n<div style=\"text-align: center;\"><b>\r\nИнформация для заказа</b></div>\r\n</div>\r\n<div>\r\n<table border=\"1\" width=\"100%\" cellspacing=\"1\" cellpadding=\"1\">\r\n<tbody>\r\n<tr>\r\n<td><b>Наименование</b></td>\r\n<td><b>Вес упаковки, кг</b></td>\r\n<td><b>Вес палеты, кг</b></td>\r\n<td><b>Минимальный заказ, кг\r\n(при отсутствии на складе)</b></td>\r\n<td><b>Артикул</b></td>\r\n</tr>\r\n<tr>\r\n<td colspan=\"1\">Сварочный электрод ESAB ЦЛ-39 2,5х350 mm</td>\r\n<td>5,0</td>\r\n<td>960,0</td>\r\n<td>5,0</td>\r\n<td colspan=\"1\">3916253YH0</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</div>\r\n</div>\r\n</div>','Сварочный электрод ESAB ЦЛ-39','','publish','closed','closed','','svarochnyj-elektrod-esab-tsl-39','','','2015-05-12 21:45:28','2015-05-12 18:45:28','',0,'http://ngsgroup/?p=2278',0,'post','',0);
