INSERT INTO `wp_posts` VALUES (2358,1,'2015-05-12 23:04:23','2015-05-12 20:04:23','<strong><span class=\"name\">Описание:</span></strong>\r\n\r\n<b>Тип покрытия – основное.</b>\r\n\r\nNi-Cu легированные электроды, предназначенные для сварки сталей стойких к атмосферной коррозии типа COR-TEN, Patinax, Dillicor. Наплавленный металл обладает повышенной стойкостью к коррозии в слабоагрессивных средах, таких как морская вода и при контакте с выхлопными газами с высоким содержанием сернистых соединений. Данные электроды также рекомендуется применять для сварки мостовых конструкций и корпусов судов, изготавливаемых из низколегированных сталей повышенной прочности типа 10ХСНД и 15ХСНД, которые также обладают повышенной стойкостью к атмосферной коррозии.\r\nТок: ~ / = (+ / ? )\r\nПространственные положения при сварке: 1, 2, 3, 4, 6\r\nНапряжение холостого хода: 65В\r\nРежимы прокалки: 330-370°С, 2 часа\r\n<table border=\"1\" width=\"100%\" cellspacing=\"1\" cellpadding=\"1\">\r\n<tbody>\r\n<tr>\r\n<td>Классификация</td>\r\n<td>Сертификация</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<div>EN ISO 2560-A: E 46 5 Z B 3 2\r\nAWS A5.5: E8018-G\r\nГОСТ 9467: Э50А (условно)</div></td>\r\n<td>ABS: 3YH5\r\nBV: 3YH10\r\nDNV: III YH10\r\nGL: 3YH15\r\nLR: 3YH15\r\nRS: 3YH10</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<div style=\"text-align: center;\"><b>Химический состав</b></div>\r\n<div>\r\n<table border=\"1\" width=\"100%\" cellspacing=\"1\" cellpadding=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td>С</td>\r\n<td>Mn</td>\r\n<td>Si</td>\r\n<td>Ni</td>\r\n<td>Cu</td>\r\n<td>P</td>\r\n<td>S</td>\r\n</tr>\r\n<tr>\r\n<td>0.06</td>\r\n<td>1.00</td>\r\n<td>0.30</td>\r\n<td>0.80</td>\r\n<td>0.40</td>\r\n<td>max 0.020</td>\r\n<td>max 0.020</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<div style=\"text-align: center;\"><b>Механические свойства</b></div>\r\n<div>\r\n<table border=\"1\" width=\"100%\" cellspacing=\"1\" cellpadding=\"1\">\r\n<tbody>\r\n<tr>\r\n<td>Предел текучести σ<sub>т</sub>, Н/мм²</td>\r\n<td>Предел прочности ?<sub>в</sub>, Н/мм?</td>\r\n<td>Удлинение δ, %</td>\r\n<td>Ударная вязкость KCV, Дж/см?</td>\r\n</tr>\r\n<tr>\r\n<td>500</td>\r\n<td>590</td>\r\n<td>27</td>\r\n<td>\r\n<div>200 при -20°С</div>\r\n163 при -40°С\r\n88 при -50°С</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<div style=\"text-align: center;\"><b>Информация для заказа</b></div>\r\n<div>\r\n<table border=\"1\" width=\"100%\" cellspacing=\"1\" cellpadding=\"1\">\r\n<tbody>\r\n<tr>\r\n<td>Наименование</td>\r\n<td>Артикул</td>\r\n</tr>\r\n<tr>\r\n<td colspan=\"1\">OK 73.08 2.5x350mm 1/4 VP</td>\r\n<td colspan=\"1\">73082531K0</td>\r\n</tr>\r\n<tr>\r\n<td>OK 73.08 3.2x350mm 1/2 VP</td>\r\n<td>73083230G0</td>\r\n</tr>\r\n<tr>\r\n<td colspan=\"1\">OK 73.08 4.0x450mm 1/2 VP</td>\r\n<td>73084040G0</td>\r\n</tr>\r\n<tr>\r\n<td colspan=\"1\">OK 73.08 5.0x450mm 1/2 VP</td>\r\n<td colspan=\"1\">73085040G0</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</div>\r\n</div>\r\n</div>','Сварочный электрод ESAB OK 73.08','','publish','closed','closed','','svarochnyj-elektrod-esab-ok-73-08','','','2015-05-12 23:04:23','2015-05-12 20:04:23','',0,'http://ngsgroup/?p=2358',0,'post','',0);
INSERT INTO `wp_posts` VALUES (2359,1,'2015-05-12 23:10:20','2015-05-12 20:10:20','<strong><span class=\"name\">Описание:</span></strong>\r\n\r\n<b>Тип покрытия – основное.</b>\r\n\r\nЭлектрод с великолепными сварочно-технологическими свойствами, обеспечивающий в наплавленном слое не более 1%Ni и гарантирующий высокие показатели ударной вязкость при температурах до -60°С. Предназначен преимущественно для сварки заполняющих и облицовочного слоёв неповоротных стыков трубопроводов класса прочности до API 5L Х70 в положении вертикаль на подъем.\r\n\r\nЭлектроды диаметром 3,2 и 4,0 мм предназначены для сварки заполняющих и облицовочного слоев шва труб класса прочности до К60 (Х70) включительно, к которым предъявляются повышенные требования по ударной вязкости при пониженных температурах (-40°С и ниже).\r\n\r\nPipeweld 8016 – это хладостойкий вариант ОК 74.70 для низкотемпературных объектов. Данные электроды отличаются отличными сварочно-технологическими свойствами, высокими значениями ударной вязкости металла шва, большим коэффициентом наплавки и предназначены для сварки толстостенных труб.\r\n\r\nТок: ~ / = (+ / ? )\r\nПространственные положения при сварке: 1, 2, 3, 4, 6\r\nНапряжение холостого хода: 65В\r\nРежимы прокалки: 330-370°С, 2 часа\r\n<table border=\"1\" width=\"100%\" cellspacing=\"1\" cellpadding=\"1\">\r\n<tbody>\r\n<tr>\r\n<td>Классификация</td>\r\n<td>Сертификация</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<div>EN ISO 2560-A: E 50 6 Mn1Ni B 1 2 H5\r\nAWS A5.5: E8016-G\r\nГОСТ 9467: Э55 (условно)</div></td>\r\n<td>НАКС: Ø 3.2; 4.0 мм\r\nВНИИГаз</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<div style=\"text-align: center;\"><b>Химический состав</b></div>\r\n<div>\r\n<table border=\"1\" width=\"100%\" cellspacing=\"1\" cellpadding=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td>С</td>\r\n<td>Mn</td>\r\n<td>Si</td>\r\n<td>Ni</td>\r\n<td>P</td>\r\n<td>S</td>\r\n</tr>\r\n<tr>\r\n<td>0.06</td>\r\n<td>1.65</td>\r\n<td>0.35</td>\r\n<td>0.80</td>\r\n<td>max 0.020</td>\r\n<td>max 0.015</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<div style=\"text-align: center;\"><b>Механические свойства</b></div>\r\n<div>\r\n<table border=\"1\" width=\"100%\" cellspacing=\"1\" cellpadding=\"1\">\r\n<tbody>\r\n<tr>\r\n<td>Предел текучести σ<sub>т</sub>, Н/мм²</td>\r\n<td>Предел прочности ?<sub>в</sub>, Н/мм?</td>\r\n<td>Удлинение δ, %</td>\r\n<td>Ударная вязкость KCV, Дж/см?</td>\r\n</tr>\r\n<tr>\r\n<td>≥500</td>\r\n<td>610</td>\r\n<td>?22</td>\r\n<td>\r\n<div>188 при -20°С</div>\r\n125 при -40°С\r\n75 при -60°С</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<div style=\"text-align: center;\"><b>Информация для заказа</b></div>\r\n<div>\r\n<table border=\"1\" width=\"100%\" cellspacing=\"1\" cellpadding=\"1\">\r\n<tbody>\r\n<tr>\r\n<td>Наименование</td>\r\n<td>Артикул</td>\r\n</tr>\r\n<tr>\r\n<td colspan=\"1\">Pipeweld 8016 3.2mm</td>\r\n<td colspan=\"1\">7Р883230G0</td>\r\n</tr>\r\n<tr>\r\n<td colspan=\"1\">Pipeweld 8016 4.0mm</td>\r\n<td colspan=\"1\">7Р884030G0</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</div>\r\n</div>\r\n</div>','Сварочный электрод ESAB Pipeweld 8016','','publish','closed','closed','','svarochnyj-elektrod-esab-pipeweld-8016','','','2015-05-12 23:10:20','2015-05-12 20:10:20','',0,'http://ngsgroup/?p=2359',0,'post','',0);
