INSERT INTO `wp_posts` VALUES (2416,1,'2015-05-13 23:17:47','2015-05-13 20:17:47','<strong><span class=\"name\">Описание:</span></strong>\n\n<b>Тип покрытия – основное.</b>\n\nЭлектроды предназначены преимущественно для сварки заполняющих и облицовочного слоёв поворотных и неповоротных стыков трубопроводов в положении вертикаль на подъем класса прочности К55-К60 (API 5L Х60-Х70), а также других ответственных конструкций нормативным пределом прочности от 539 до 589 МПа.\nТок: = (+)\nПространственные положения при сварке: 1, 2, 3, 4, 6\nРежимы прокалки: 360-400°С, 1 час\n\nС 2015 года электрод производится на заводе ЗАО \"ЭСАБ-СВЭЛ\", входящий в группу компаний ESAB.\n<table border=\"1\" width=\"100%\" cellspacing=\"1\" cellpadding=\"1\">\n<tbody>\n<tr>\n<td>Классификация</td>\n<td>Сертификация</td>\n</tr>\n<tr>\n<td>\n<div>ГОСТ 9467: Э60\nГОСТ Р ИСО 2560-A: E 46 4 1NiMo B 2 2 H10\nAWS A5.1: E8016-G</div></td>\n<td>НАКС: Ø 3.0; 4.0 мм\nВНИИГаз\nRS: 3Y40HН</td>\n</tr>\n</tbody>\n</table>\n<div style=\"text-align: center;\"><b>Химический состав</b></div>\n<div>\n<table border=\"1\" width=\"100%\" cellspacing=\"1\" cellpadding=\"1\" align=\"center\">\n<tbody>\n<tr>\n<td>С</td>\n<td>Mn</td>\n<td>Si</td>\n<td>Ni</td>\n<td>Mo</td>\n<td>P+S</td>\n<td>P</td>\n<td>S</td>\n</tr>\n<tr>\n<td>0.06</td>\n<td>1.10</td>\n<td>0.45</td>\n<td>0.70</td>\n<td>0.40</td>\n<td>max 0.035</td>\n<td>max 0.025</td>\n<td>max 0.025</td>\n</tr>\n</tbody>\n</table>\n<div style=\"text-align: center;\"><b>Механические свойства</b></div>\n<div>\n<table border=\"1\" width=\"100%\" cellspacing=\"1\" cellpadding=\"1\">\n<tbody>\n<tr>\n<td>Предел текучести σ<sub>т</sub>, Н/мм²</td>\n<td>Предел прочности σ<sub>в</sub>, Н/мм²</td>\n<td>Удлинение δ, %</td>\n<td>Ударная вязкость KCV, Дж/см²</td>\n<td>Ударная вязкость KCU, Дж/см²</td>\n</tr>\n<tr>\n<td>≥460</td>\n<td>≥590</td>\n<td>≥22</td>\n<td>\n<div>≥120 при +20°С\n≥59 при -40°С\n≥34 при -60°С</div></td>\n<td>≥130 при +20°С\n≥50 при -60°С</td>\n</tr>\n</tbody>\n</table>\n<div style=\"text-align: center;\"><b>Информация для заказа</b></div>\n<div>\n<table border=\"1\" width=\"100%\" cellspacing=\"1\" cellpadding=\"1\">\n<tbody>\n<tr>\n<td><b>Наименование</b></td>\n<td><b>Вес упаковки, кг</b></td>\n<td><b>Вес палеты, кг</b></td>\n<td><b>Минимальный заказ, кг\n(при отсутствии на складе)</b></td>\n<td><b>Артикул</b></td>\n</tr>\n<tr>\n<td colspan=\"1\">Сварочный электрод ESAB МТГ-03 3,0х350 mm</td>\n<td>4,5</td>\n<td>810,0</td>\n<td>4,5</td>\n<td colspan=\"1\">3934303WM0</td>\n</tr>\n<tr>\n<td>Сварочный электрод ESAB МТГ-03 4,0х450 mm</td>\n<td>6,0</td>\n<td>864,0</td>\n<td>6,0</td>\n<td>3934404WM0</td>\n</tr>\n</tbody>\n</table>\n&nbsp;\n\n</div>\n</div>\n</div>','Сварочный электрод ESAB МТГ-03','','inherit','closed','open','','2392-autosave-v1','','','2015-05-13 23:17:47','2015-05-13 20:17:47','',2392,'http://ngsgroup/bez-rubriki/2392-autosave-v1/',0,'revision','',0);
