INSERT INTO `wp_posts` VALUES (2585,1,'2015-05-14 22:50:19','2015-05-14 19:50:19','<strong><span class=\"name\">Описание:</span></strong>\r\n\r\nУниверсальная сварочная проволока ESAB <b>Weld G3Si1 </b>(аналог OK Autrod 12.51 и Св08Г2С), предназначенная для сварки изделий из конструкционных нелегированных и низколегированных сталей с пределом текучести до 500/470 МПа, эксплуатирующихся при знакопеременных нагрузках и низких температурах. Высококачественное омеднение, рядная намотка на металлические катушки, стабильный диаметр по всей длине в сочетании с низким содержанием вредных примесей, таких как S и P, обеспечивают стабильное горение проволоки с минимальным разбрызгиванием и высокое качество наплавленного металла. Проволока нашла широкое применение в судостроении, сварке металлоконструкций, машиностроении и многих других отраслях промышленности. Упакована в полиэтиленовый пакет и запечатана в коробку коричневого цвета с логотипом ЭСАБ.\r\nДанная проволока является бюджетным вариантом OK Autrode 12.51 с незначительным снижением пластических характеристик наплавленного металла при отрицательных температурах.\r\nВыпускаемые диаметры: от 0,8 до 1,2 мм','Сварочная проволока ESAB Weld G3Si1','','publish','closed','closed','','svarochnaya-provoloka-esab-weld-g3si1','','','2015-05-14 22:50:37','2015-05-14 19:50:37','',0,'http://ngsgroup/?p=2585',0,'post','',0);
