INSERT INTO `wp_posts` VALUES (2610,1,'2015-05-14 23:24:00','2015-05-14 20:24:00','<strong><span class=\"name\">Описание:</span></strong>\r\n\r\nНеомедненная Ni-Cu легированная сварочная проволока с уникальной обработкой поверхности ASC (Advanced Surface Characteristics – поверхность с улучшенными характеристиками), предназначенная для сварки сталей стойких к атмосферной коррозии типа COR-TEN, Patinax, Dillicor. Наплавленный металл обладает повышенной стойкостью к коррозии в слабоагрессивных средах, таких как морская вода и при контакте с газами с высоким содержанием сернистых соединений. Проволока также рекомендуется для сварки мостовых конструкций и корпусов судов, изготавливаемых из низколегированных сталей повышенной прочности типа 10ХСНД и 15ХСНД, а также других низколегированных сталей с пределом текучести до 470 МПа, которые обладают повышенной стойкостью к атмосферной коррозии.\r\nВыпускаемые диаметры: 0,8; 1,0 и 1,2 мм\r\n<table border=\"1\" width=\"100%\" cellspacing=\"1\" cellpadding=\"1\">\r\n<tbody>\r\n<tr>\r\n<td>Классификация</td>\r\n<td>Сертификация</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<div>EN ISO 14341-A: G 42 0 C1 Z 3Ni1Cu\r\nEN ISO 14341-A: G 46 4 M21 Z 3Ni1Cu\r\nAWS A5.28: ER70S-G</div></td>\r\n<td>DNV: II YMS (C1)\r\nDNV: III YMS (M21)</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<div style=\"text-align: center;\"><b>Химический состав</b></div>\r\n<div>\r\n<table border=\"1\" width=\"100%\" cellspacing=\"1\" cellpadding=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td>С</td>\r\n<td>Mn</td>\r\n<td>Si</td>\r\n<td>Ni</td>\r\n<td>Cu</td>\r\n<td>P</td>\r\n<td>S</td>\r\n</tr>\r\n<tr>\r\n<td>0.08-0.11</td>\r\n<td>1.25-1.55</td>\r\n<td>0.70-0.90</td>\r\n<td>0.80-0.90</td>\r\n<td>0.25-0.60</td>\r\n<td>max 0.025</td>\r\n<td>max 0.025</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<div style=\"text-align: center;\"><b>Механические свойства</b></div>\r\n<div>\r\n<table border=\"1\" width=\"100%\" cellspacing=\"1\" cellpadding=\"1\">\r\n<tbody>\r\n<tr>\r\n<td>Предел текучести σ<sub>т</sub>, Н/мм²</td>\r\n<td>Предел прочности ?<sub>в</sub>, Н/мм?</td>\r\n<td>Удлинение δ, %</td>\r\n<td>Ударная вязкость KCV, Дж/см?</td>\r\n</tr>\r\n<tr>\r\n<td colspan=\"4\">Защитный газ - M21 (80%Ar + 20%CO<span style=\"font-size: xx-small;\">2</span>)</td>\r\n</tr>\r\n<tr>\r\n<td>540</td>\r\n<td>625</td>\r\n<td>26</td>\r\n<td>175 при +20°С\r\n<div>138  при -20°С\r\n104  при -40°С\r\n63  при -60°С</div></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<div style=\"text-align: center;\"><b>Информация для заказа</b></div>\r\n<div>\r\n<table border=\"1\" width=\"100%\" cellspacing=\"1\" cellpadding=\"1\">\r\n<tbody>\r\n<tr>\r\n<td>Наименование</td>\r\n<td>Артикул</td>\r\n</tr>\r\n<tr>\r\n<td colspan=\"1\">OK AristoRod 13.26 0.8mm 15kg</td>\r\n<td colspan=\"1\">1B26086900</td>\r\n</tr>\r\n<tr>\r\n<td colspan=\"1\">OK AristoRod 13.26 1.0mm 18kg</td>\r\n<td colspan=\"1\">1B26106910</td>\r\n</tr>\r\n<tr>\r\n<td colspan=\"1\">OK AristoRod 13.26 1.2mm 18kg</td>\r\n<td colspan=\"1\">1B26126910</td>\r\n</tr>\r\n<tr>\r\n<td colspan=\"1\">OK AristoRod 13.26 1.2mm 250kg</td>\r\n<td colspan=\"1\">1B26129320</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</div>\r\n</div>\r\n</div>','Сварочная проволока ESAB ОК AristoRod 13.26','','publish','closed','closed','','svarochnaya-provoloka-esab-ok-aristorod-13-26','','','2015-05-14 23:24:00','2015-05-14 20:24:00','',0,'http://ngsgroup/?p=2610',0,'post','',0);
